import {halfCompareTapper, tapper} from "./tapper";
import {subtractByModule} from "./actions/subtract";
import {addByModule} from "./actions/add";

const a = '44324234234234365498678562423423423';
const k = '';

console.time('tapper');
let i: number = 0;
const matrix: string[][] = [];
for (let y: string = k; parseInt(subtractByModule(y, k))<=17; y = addByModule(y, '1')) {
  matrix.push([]);
  for(let x: number = 0; x <= 106; ++x) {
    matrix[i].push(halfCompareTapper(x.toString(), y) ? '*' : ' ');
  }
  i++;
}
console.timeEnd('tapper');

matrix.forEach(m => {
  console.log(m.reduce((acc, cur) => `${acc}${cur}`));
});

console.log(`Matrix length: ${matrix.length}`);